import React, {useState, useEffect} from 'react';
import { Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
//import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
//import Checkbox from '@material-ui/core/Checkbox';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import FolderIcon from '@material-ui/icons/Folder';
import AddIcon from '@material-ui/icons/Add';
import SettingsCellIcon from '@material-ui/icons/SettingsCell';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    maxWidth: 752,
  },
  demo: {
    backgroundColor: theme.palette.background.paper,
  },
  title: {
    margin: theme.spacing(4, 0, 2),
  },
}));


function App() {
    const classes = useStyles();

    const [produtos, setProdutos] = useState([
      {id:1, nome:'Apple 1', preco:5000.00 , quantidade:1},
      {id:2, nome:'Xiaomi', preco:3000.00 , quantidade:0},
      {id:3, nome:'Samsung', preco:4000.00 , quantidade:0},
      {id:4, nome:'Motorola', preco:3000.00 , quantidade:0},
    ]);
    function handleAddProduto(){
      setProdutos([
        ... produtos,
        {id:5, nome:'Positivo', preco:200000, quantidade:0},
      ]);
    }

    // const [compras, setCompras] = useState([

    // ]);
    useEffect(()=>{
          //poderia ser buscado via Axios por exemplo 
          //response =produtos;
          data = produtos;
    }, []);

    //
    function handleAddCar(){
      setProdutos([
        ... produtos,
        {id:5, nome:'Positivo', preco:200000, quantidade:0},
      ]);
    }
  //   const total = produtos.reduce((quantidade, preco) => {
  //     return ((quantidade * preco));
  // }, 0);
  const total = produtos.reduce((resultado, teste) => {
    return (resultado + (teste.preco*teste.quantidade) );
}, 0);

  // return <Button color="primary">Carrinho</Button>;
  return (    
    <div className={classes.root}>
      <FormGroup row>
        {/* <FormControlLabel
          control={
            <Checkbox checked={dense} onChange={(event) => setDense(event.target.checked)} />
          }
          label="Enable dense"
        /> */}
        {/* <FormControlLabel
          control={

          }
          label="Enable secondary text"
        /> */}
      </FormGroup>
     <Grid item xs={12} md={6}>
          <Typography variant="h6" className={classes.title}>
            Lista de produtos a venda
          </Typography>
          <div className={classes.demo}>
            <List produto={produtos}>
              { produtos.map(r=> 
                <ListItem>
                  <ListItemAvatar>
                    <Avatar>
                      <SettingsCellIcon />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                   primary= {r.nome}
                  // { quantidade ? 'Secondary text' : null}
                  />
                  <ListItemText
                   primary= {r.preco}
                  // { quantidade ? 'Secondary text' : null}
                  />
                  <ListItemSecondaryAction>
                    <IconButton edge="end" aria-label="Comprar">
                      <AddIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>,
              )}
            </List>
          </div>
        </Grid>
      {/* <ul>
        {
          produtos.map(r=> 
                <li key={r.id}>{r.nome}
                </li> 
          )
        }
      </ul> */}
      {/* <button  color="primary" onClick={handleAddCar}> Adicionar Celular Positivo</button> */}


      <label htmlFor="contained-button-file">        
        <Button variant="contained" color="primary" component="span" onClick={() => { alert('Finalizado') }}>Finalizar</Button>
        <Button variant="contained" color="secondary" component="span" >Total a pagar  {total}</Button>

      </label>
      </div>
    );
}

export default App;
