//import React from 'react';

import React, {useState} from 'react';
import { Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
//import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
//import FormGroup from '@material-ui/core/FormGroup';
//import FormControlLabel from '@material-ui/core/FormControlLabel';
//import Checkbox from '@material-ui/core/Checkbox';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import FolderIcon from '@material-ui/icons/Folder';
import DeleteIcon from '@material-ui/icons/Delete'; 
import SettingsCellIcon from '@material-ui/icons/SettingsCell';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    maxWidth: 752,
  },
  demo: {
    backgroundColor: theme.palette.background.paper,
  },
  title: {
    margin: theme.spacing(4, 0, 2),
  },
}));


function App() {
    const classes = useStyles();

    const [produtos, setRepositories] = useState([
      {id:1, nome:'Apple 1', preco:'5' , quantidade:0},
      {id:2, nome:'Xiaomi', preco:'3' , quantidade:0},
      {id:3, nome:'Samsung', preco:'4' , quantidade:0},
      {id:4, nome:'Motorola', preco:'3' , quantidade:0},
    ]);

    //
    function handleAddCar(){
      setRepositories([
        ... produtos,
        {id:5, nome:'Positivo', preco:'2' },
      ]);
    }

  // return <Button color="primary">Carrinho</Button>;
  return (
    <>
     <Grid item xs={12} md={6}>
          <Typography variant="h6" className={classes.title}>
            Avatar with text and icon
          </Typography>
          <div className={classes.demo}>
            <List produto={produtos}>
              { produtos.map(r=> 
                <ListItem>
                  <ListItemAvatar>
                    <Avatar>
                      <SettingsCellIcon />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                   primary= {r.nome}
                  // { quantidade ? 'Secondary text' : null}
                  />
                  <ListItemSecondaryAction>
                    <IconButton edge="end" aria-label="Comprar">
                      <ShoppingCartIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>,
              )}
            </List>
          </div>
        </Grid>
      <ul>
        {
          produtos.map(r=> 
                <li key={r.id}>{r.nome}
                </li> 
          )
        }
      </ul>
      <button  color="primary" onClick={handleAddCar}> Adc Novo</button>
    </>
    );
}

export default App;
